def pickups(target,digit=5)
  s = target.to_s
  num = 0
  a = []
  loop do
    element = s[num, digit]
    break if element.length < digit
    a << element
    num += 1
  end
  a
end

def search_max(arr)
  max_val = 0
  arr.each do |i|
    result = i[0].to_i * i[1].to_i * i[2].to_i * i[3].to_i * i[4].to_i
    max_val = max_val < result ? result : max_val
  end
  puts max_val
end

